import { useState, useEffect } from 'react';
import { getOrGenerateThumbnail } from '@/lib/videoThumbnailCache';

/**
 * Hook to get or generate a video thumbnail
 */
export function useVideoThumbnail(
  videoId: string,
  videoUrl: string,
  enabled: boolean = true
): { thumbnailUrl: string | null; isLoading: boolean; error: Error | null } {
  const [thumbnailUrl, setThumbnailUrl] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!enabled || !videoId || !videoUrl) {
      return;
    }

    let isMounted = true;
    let objectUrl: string | null = null;

    async function loadThumbnail() {
      setIsLoading(true);
      setError(null);

      try {
        const url = await getOrGenerateThumbnail(videoId, videoUrl, 400);

        if (isMounted) {
          objectUrl = url;
          setThumbnailUrl(url);
          setIsLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err as Error);
          setIsLoading(false);
        }
      }
    }

    loadThumbnail();

    return () => {
      isMounted = false;
      // Clean up object URL if it was created
      if (objectUrl && objectUrl.startsWith('blob:')) {
        URL.revokeObjectURL(objectUrl);
      }
    };
  }, [videoId, videoUrl, enabled]);

  return { thumbnailUrl, isLoading, error };
}

