/**
 * Video Thumbnail Cache using IndexedDB
 * Stores generated video thumbnails as blobs for faster grid loading
 */

const DB_NAME = 'VideoThumbnailCache';
const STORE_NAME = 'thumbnails';
const DB_VERSION = 1;

interface ThumbnailEntry {
  videoId: string;
  blob: Blob;
  timestamp: number;
  width: number;
  height: number;
}

let dbPromise: Promise<IDBDatabase> | null = null;

function openDB(): Promise<IDBDatabase> {
  if (dbPromise) return dbPromise;

  dbPromise = new Promise((resolve, reject) => {
    const request = indexedDB.open(DB_NAME, DB_VERSION);

    request.onerror = () => reject(request.error);
    request.onsuccess = () => resolve(request.result);

    request.onupgradeneeded = (event) => {
      const db = (event.target as IDBOpenDBRequest).result;

      // Create object store if it doesn't exist
      if (!db.objectStoreNames.contains(STORE_NAME)) {
        const store = db.createObjectStore(STORE_NAME, { keyPath: 'videoId' });
        store.createIndex('timestamp', 'timestamp', { unique: false });
      }
    };
  });

  return dbPromise;
}

/**
 * Generate a thumbnail from a video URL
 */
export async function generateThumbnail(
  videoUrl: string,
  targetWidth: number = 400
): Promise<Blob> {
  return new Promise((resolve, reject) => {
    const video = document.createElement('video');
    video.crossOrigin = 'anonymous';
    video.preload = 'metadata';
    video.muted = true;
    video.playsInline = true;

    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    if (!ctx) {
      reject(new Error('Failed to get canvas context'));
      return;
    }

    let resolved = false;
    const timeout = setTimeout(() => {
      if (!resolved) {
        video.src = '';
        reject(new Error('Thumbnail generation timeout'));
      }
    }, 10000); // 10 second timeout

    video.addEventListener('loadedmetadata', () => {
      // Seek to 1 second or 10% of duration, whichever is smaller
      const seekTime = Math.min(1, video.duration * 0.1);
      video.currentTime = seekTime;
    });

    video.addEventListener('seeked', () => {
      if (resolved) return;

      try {
        // Calculate dimensions maintaining aspect ratio
        const aspectRatio = video.videoWidth / video.videoHeight;
        canvas.width = targetWidth;
        canvas.height = targetWidth / aspectRatio;

        // Draw video frame to canvas
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Convert to blob
        canvas.toBlob(
          (blob) => {
            clearTimeout(timeout);
            if (blob && !resolved) {
              resolved = true;
              video.src = '';
              resolve(blob);
            } else {
              reject(new Error('Failed to create blob'));
            }
          },
          'image/jpeg',
          0.85 // Quality
        );
      } catch (err) {
        clearTimeout(timeout);
        video.src = '';
        reject(err);
      }
    });

    video.addEventListener('error', (e) => {
      clearTimeout(timeout);
      video.src = '';
      reject(new Error(`Video load error: ${video.error?.message || 'Unknown error'}`));
    });

    video.src = videoUrl;
  });
}

/**
 * Get cached thumbnail for a video
 */
export async function getCachedThumbnail(videoId: string): Promise<string | null> {
  try {
    const db = await openDB();
    const transaction = db.transaction(STORE_NAME, 'readonly');
    const store = transaction.objectStore(STORE_NAME);

    return new Promise((resolve, reject) => {
      const request = store.get(videoId);

      request.onsuccess = () => {
        const entry = request.result as ThumbnailEntry | undefined;
        if (entry && entry.blob) {
          // Convert blob to object URL
          const url = URL.createObjectURL(entry.blob);
          resolve(url);
        } else {
          resolve(null);
        }
      };

      request.onerror = () => reject(request.error);
    });
  } catch (err) {
    console.error('Error getting cached thumbnail:', err);
    return null;
  }
}

/**
 * Cache a thumbnail
 */
export async function cacheThumbnail(
  videoId: string,
  blob: Blob,
  width: number,
  height: number
): Promise<void> {
  try {
    const db = await openDB();
    const transaction = db.transaction(STORE_NAME, 'readwrite');
    const store = transaction.objectStore(STORE_NAME);

    const entry: ThumbnailEntry = {
      videoId,
      blob,
      timestamp: Date.now(),
      width,
      height,
    };

    return new Promise((resolve, reject) => {
      const request = store.put(entry);
      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  } catch (err) {
    console.error('Error caching thumbnail:', err);
    throw err;
  }
}

/**
 * Get or generate thumbnail
 */
export async function getOrGenerateThumbnail(
  videoId: string,
  videoUrl: string,
  targetWidth: number = 400
): Promise<string> {
  // Try to get from cache first
  const cached = await getCachedThumbnail(videoId);
  if (cached) {
    console.log(`✓ Using cached thumbnail for ${videoId}`);
    return cached;
  }

  // Generate new thumbnail
  console.log(`⏳ Generating thumbnail for ${videoId}`);
  try {
    const blob = await generateThumbnail(videoUrl, targetWidth);
    const aspectRatio = blob.size > 0 ? 1 : 1; // Will be calculated from canvas
    await cacheThumbnail(videoId, blob, targetWidth, targetWidth / aspectRatio);

    const url = URL.createObjectURL(blob);
    console.log(`✓ Generated and cached thumbnail for ${videoId}`);
    return url;
  } catch (err) {
    console.error(`Failed to generate thumbnail for ${videoId}:`, err);
    // Return video URL as fallback
    return videoUrl;
  }
}

/**
 * Clear old thumbnails (older than 30 days)
 */
export async function clearOldThumbnails(maxAge: number = 30 * 24 * 60 * 60 * 1000): Promise<void> {
  try {
    const db = await openDB();
    const transaction = db.transaction(STORE_NAME, 'readwrite');
    const store = transaction.objectStore(STORE_NAME);
    const index = store.index('timestamp');

    const cutoffTime = Date.now() - maxAge;
    const range = IDBKeyRange.upperBound(cutoffTime);

    return new Promise((resolve, reject) => {
      const request = index.openCursor(range);

      request.onsuccess = (event) => {
        const cursor = (event.target as IDBRequest).result;
        if (cursor) {
          cursor.delete();
          cursor.continue();
        } else {
          resolve();
        }
      };

      request.onerror = () => reject(request.error);
    });
  } catch (err) {
    console.error('Error clearing old thumbnails:', err);
  }
}

/**
 * Clear all thumbnails
 */
export async function clearAllThumbnails(): Promise<void> {
  try {
    const db = await openDB();
    const transaction = db.transaction(STORE_NAME, 'readwrite');
    const store = transaction.objectStore(STORE_NAME);

    return new Promise((resolve, reject) => {
      const request = store.clear();
      request.onsuccess = () => {
        console.log('✓ Cleared all thumbnails from cache');
        resolve();
      };
      request.onerror = () => reject(request.error);
    });
  } catch (err) {
    console.error('Error clearing thumbnails:', err);
  }
}

/**
 * Get cache statistics
 */
export async function getCacheStats(): Promise<{ count: number; totalSize: number }> {
  try {
    const db = await openDB();
    const transaction = db.transaction(STORE_NAME, 'readonly');
    const store = transaction.objectStore(STORE_NAME);

    return new Promise((resolve, reject) => {
      const request = store.getAll();

      request.onsuccess = () => {
        const entries = request.result as ThumbnailEntry[];
        const totalSize = entries.reduce((sum, entry) => sum + entry.blob.size, 0);

        resolve({
          count: entries.length,
          totalSize,
        });
      };

      request.onerror = () => reject(request.error);
    });
  } catch (err) {
    console.error('Error getting cache stats:', err);
    return { count: 0, totalSize: 0 };
  }
}

