import { useVideoThumbnail } from '@/hooks/useVideoThumbnail';

interface VideoThumbnailProps {
  videoId: string;
  videoUrl: string;
  className?: string;
  alt?: string;
}

export default function VideoThumbnail({ videoId, videoUrl, className, alt }: VideoThumbnailProps) {
  const { thumbnailUrl, isLoading } = useVideoThumbnail(videoId, videoUrl, true);

  if (isLoading || !thumbnailUrl) {
    return (
      <div className={`${className} bg-muted animate-pulse flex items-center justify-center`}>
        <div className="text-muted-foreground text-xs">Loading...</div>
      </div>
    );
  }

  return (
    <img
      src={thumbnailUrl}
      alt={alt || 'Video thumbnail'}
      className={className}
      loading="lazy"
    />
  );
}
